(function($) {

	$.noty.defaults.template = '<div class="alert noty_message"><a class="close noty_close" title="Close" style="cursor: pointer;"><i class="icon-cancel"></i></a><p class="message noty_text"></p></div>';
	$.noty.defaults.closeWith = ['button'];

// Fade in/out
 	$.noty.defaults.animation = {
        open: {opacity: 'toggle'},
        close: {opacity: 'toggle'},
        easing: 'swing',
        speed: 1000
    };

	$.noty.themes.defaultTheme = {
		name: 'h2oTheme',
		modal: {
			css: {
				position: 'fixed',
				width: '100%',
				height: '100%',
				backgroundColor: '#000',
				zIndex: 10000,
				opacity: 0.6,
				display: 'none',
				left: 0,
				top: 0
			}
		},
		style: function() {
			// Setup spacing between items
			this.$bar.css({
				margin: "20px 0 20px 0"
			});

			// Setup close button fade in/out on hover.
			this.$bar.bind({
				mouseenter: function() { $(this).find('.noty_close').stop().fadeTo('normal',1); },
				mouseleave: function() { $(this).find('.noty_close').stop().fadeTo('normal',0); }
			});

			// Setup close button initial fade out, so that the user knows it's there.
			this.$closeButton.find('.noty_close').stop().fadeTo('normal',0);

			// Add alert type as a class
			this.$message.addClass(this.options.type);
		},
		callback: {
			onShow: function() { },
			onClose: function() { }
		}
	};

})(jQuery);

